INSERT INTO `ic_noticia` VALUES (1,20160825,1,'','','Indicaciones para el revisor(a)','Estimado $name,\r\n\r\nRecientemente se presentó una nueva propuesta al comité y usted ha sido seleccionado como evaluador de este proyecto titulado &quot;$title&quot;.\r\n\r\nProtocol: &lt;B&gt;$protocol&lt;/B&gt;\r\n\r\nPara tener acceso a este proyecto, por favor ingrese al sistema. Si tiene cualquier pregunta o duda, contáctenos a XXX@XXX\r\n\r\nSaludos cordiales,\r\n\r\n&lt;B&gt;Comité de ética&lt;/B&gt;\r\n\r\n$link',19000101,19000101,NULL,NULL,'indicate_email',NULL,'es',0,0),(2,20160823,1,'','','Senha info','Prezado Investigador,\r\n\r\nConforme sua solicitação foi encaminho um e-mail com sua senha.',19000101,19000101,NULL,NULL,'send_email_tex',NULL,'pt_BR',0,0),(3,20160823,1,'','','End of review process','Dear Investigador,\r\n\r\nThe review process of the protocol you submitted to this committee has finalized.\r\n\r\nPlease log into the system to access the official decision.\r\n\r\nRegards,\r\n',19000101,19000101,NULL,NULL,'communicate_investig',NULL,'en_US',0,0),(4,20160823,1,'','','Your password information','Dear User,\r\n\r\nYour password has been sent to your e-mail address on file.\r\n\r\nRegards,',19000101,19000101,NULL,NULL,'send_email_content',NULL,'en_US',0,0),(5,20160823,1,'','','Forgotten password information','Dear Investigator,\r\n\r\nAs requested, an email has been sent to you with your password information.\r\n\r\nRegards, best wishes!\r\n\r\nEthics Committee',19000101,19000101,NULL,NULL,'send_email_text',NULL,'en_US',0,0),(6,20160823,1,'','','¿Olvidó su contraseña?','Estimado investigador,\r\n\r\nTal como lo solicitó, se le ha enviado un email con la información de su contraseña.\r\n\r\nSaludos,\r\n\r\nComité de Ética',19000101,19000101,NULL,NULL,'send_email_text',NULL,'es',0,0),(7,20160823,1,'','','Instructions for reviewers','Dear $name,\r\n\r\nA new proposal was recently submitted to this committee and you have been selected as evaluator of the project titled. &quot;$title&quot;.\r\n\r\nProtocol: &lt;B&gt;$protocol&lt;/B&gt;\r\n\r\nTo access this project, please log into the system. If you have any questions or comments, please contact us at pahoerc@paho.org \r\n\r\nKind regards,\r\n\r\n&lt;B&gt;Ethics Committee&lt;/B&gt;\r\n\r\n$link',19000101,19000101,NULL,NULL,'indicate_email',NULL,'en_US',0,0),(8,20160823,1,'','','Email para asignar revisor','Estimado $name,\r\n\r\nRecientemente se presento una nueva propuesta a este comité y usted ha sido seleccionado como evaluador de este proyecto con el titulo. &quot;$title&quot;.\r\n\r\nProtocol: &lt;B&gt;$protocol&lt;/B&gt;\r\n\r\nPara tener acceso a este proyecto, por favor ingrese al sistema. Si tiene cualquier pregunta o duda, contáctenos a acondor@ins.gob.pe \r\n\r\nSaludos cordiales,\r\n\r\n&lt;B&gt;Comité de ética&lt;/B&gt;\r\n\r\n$link',19000101,19000101,NULL,NULL,'indicate_email',NULL,'es',0,0),(9,20160823,1,'','','Senha do sistema','Prezado Investigador,\r\n\r\nConforme sua solicita??oo foi encaminho um e-mail com sua senha.',19000101,19000101,NULL,NULL,'send_email_text',NULL,'pt_BR',0,0),(10,20160823,1,'','','Finalización del proceso de revisión','Estimado investigador,\r\n\r\nEl proceso de revisión del protocolo presentado a este comité ha finalizado.\r\n\r\nPor favor entre al sistema para acceder la decisión oficial.\r\n\r\nSaludos,\r\n\r\nComité de Ética',19000101,19000101,NULL,NULL,'communicate_investig',NULL,'es',0,0),(11,20160823,1,'','','Protocol returned for corrections','Dear Investigator,\r\n\r\nYour proposal presents certain issues that we need you to correct. Please revise and resubmit when corrected.\r\n\r\nKind regards,\r\n\r\nEthics Committee',19000101,19000101,NULL,NULL,'communicate_return',NULL,'en_US',0,0),(12,20160823,1,'','','Confirmación de creación de usuario','Estimado $name,\r\n($email)\r\n\r\nMuchas gracias por la creación de su usuario. Haga clic acá: $link\r\n\r\nHasta luego,\r\n\r\nComité de Ética',19000101,19000101,NULL,NULL,'email_confirm_email',NULL,'es',0,0),(13,20160823,1,'','','Protocolo asignado para su revisión','Estimado revisor,\r\n\r\nEl protocolo en mención le ha sido asignado para su revisión.\r\n\r\nPor favor ingrese al sistema ProEthos para acceder a este protocolo.\r\n\r\nSi tiene cualquier duda, por favor no dude en comunicarse con nosotros.\r\n\r\nSaludos\r\n\r\nComité de Ética',19000101,19000101,NULL,NULL,'survey_email',NULL,'es',0,0),(14,20160823,1,'','','Protocol assigned for your review','Dear reviewer,\r\n\r\nThe protocol here mentioned has been assigned to you for review.\r\n\r\nPlease go to the ProEthos system in order to access this protocol.\r\n\r\nIf you have any questions, please don/t hesitate to contact us.\r\n\r\nRegards\r\n\r\nEthics Committee',19000101,19000101,NULL,NULL,'survey_email',NULL,'en_US',0,0),(15,20160823,1,'','','Senha info','Estimado Investigador\r\n\r\nConforme a lo solicitado, se le esta enviando un correo con su contraseña.\r\n\r\nSaludos',19000101,19000101,NULL,NULL,'send_email_tex',NULL,'es',0,0),(16,20160823,1,'','','Su protocolo ha sido presentado exitosamente','$INFORMACION_DEL_PROTOCOLO\r\n\r\nEstimado investigador,\r\n\r\nEl protocolo arriba mencionado ha sido presentado a este comité exitosamente.\r\nLo mantendremos informado del avance del proceso de revisión ética de su proyecto.\r\n\r\nSaludos,\r\n\r\n$INFORMACION_DEL_COMITE',19000101,19000101,NULL,NULL,'email_confirm_subm',NULL,'es',0,0),(17,20160823,1,'','','Hay un nuevo protocolo presentado para revisión','$INFORMACION_DEL_PROTOCOLO\r\n\r\nUn nuevo protocolo ha sido presentado para revisión.\r\n\r\nPara mayor información, por favor ingrese al sistema.\r\n\r\nProEthos',19000101,19000101,NULL,NULL,'email_notify_subm',NULL,'es',0,0),(18,20160823,1,'','','La acción de seguimiento de su protocolo ha sido recibida','Estimado investigador,\r\n\r\nHemos recibido la acción de seguimiento de su protocolo.\r\n\r\nLe mantendremos informando del avance de este proceso.\r\n\r\nSaludos,\r\n\r\nComité de Ética',19000101,19000101,NULL,NULL,'email_monitoreo_subm',NULL,'es',0,0);
